import React from 'react'
import './ExpensesFilter.css'
import { styled } from 'styled-components'

const ExpensesFilter = ({props, selectedYear, onSelectedYearChange }) => {
	return (
		<div className='expenses-filter'>
			<div className='expenses-filter__control'>
				<label style={{fontSize:"30px"}} htmlFor='filter'>Filter by year:</label>

				<BtnStyle onClick={props.goDown}>По возрастанию</BtnStyle>
				<BtnStyle onClick={props.asce}>По убыванию</BtnStyle>
				<select
					id='filter'
					value={selectedYear}
					onChange={onSelectedYearChange}
				>
					<option value='2023'>2023</option>
					<option value='2022'>2022</option>
					<option value='2021'>2021</option>
					<option value='2020'>2020</option>
					<option value='All'>All</option>
				</select>
			</div>
		</div>
	)
}

export default ExpensesFilter

const BtnStyle= styled.button`
	background-color: #371139;
	border: 1px solid #ffffff;
	color: #ffffff;
	padding: 9px;

	:hover{
		background-color: #fff;
		color: #242222
	}

`
